class Command(BaseCommand):
    help = 'Reads IMDB data files into the database'

    def add_arguments(self, parser):
        parser.add_argument('file', help='file to add: titles, names, principals, or all')

    def handle(self, *args, **options):
        """Load all imdb data."""

        data = options['file']

        if data == 'titles' or data == 't':
            load_titles()
        elif data == 'names' or data == 'n':
            load_names()
        elif data == 'principals' or data == 'p':
            load_principals()
        elif data == 'all':
            load_titles()
            load_names()
            load_principals()
        # load_titles()
        # load_names()
        # load_principals()
        return None
